import React from 'react';
import { TextInput, Text, View, Image, Button } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import { LinearGradient } from "expo-linear-gradient";

import { styles } from "../signin/styles";
import { BackImg } from "../../assets/L";
import { ButtonNext } from "../../components/ButtonNext/index";

export function Signin(){
    return (
        <View style={styles.container} >
            <StatusBar 
                backgroundColor="transparent"
                translucent
            />    


            <View>           
                <Image
                    source={BackImg}
                    style={styles.image}
                    resizeMode='stretch'
                />
                <Text style={styles.title}> Bem-vindo(a)! </Text>
                <Text style={styles.subtitle}> 
                    Essa calculadora hipercalórica, {'\n'}
                    esse app irá te auxiliar a dividir {'\n'}
                    seus macronutrientes para uma dieta de ganho {'\n'}
                    de massa corporal... 
                </Text>

                <ButtonNext/>
            </View>

                    
        </View>
    );
}
